library(here)
library(targets)
library(tidyr)

data &lt;- 
  targets::tar_read("geocoded_data", store = here::here("_targets")) |&gt;
  dplyr::select(
    age, sex, state, region, latitude, longitude, height, weight, work, study,
    msf_sc, sjl, le_week, 
    ) |&gt;
  tidyr::drop_na(msf_sc, age, sex, latitude)
